const selection = {
  icons: [
    { name: "conta-simples", paths: 3 },
    { name: "daycoval", paths: 3 },
    { name: "mercado-pago", paths: 6 },
    { name: "nubank-bg", paths: 3 },
    { name: "pagseguro", paths: 7 },
    { name: "unicred-bg", paths: 2 },
    { name: "conta-simples-comp", paths: 16 },
    { name: "mercado-pago-comp", paths: 17 },
    { name: "pagbank-comp", paths: 7 },
    { name: "pagseguro-comp", paths: 8 },
  ],
};
function iconsBankColoredPaths(a) {
  let n = selection.icons,
    e = 1;
  return (
    n.forEach((n) => {
      let o = n.name;
      a.includes(o) && (e = n.paths);
    }),
    e
  );
}
function initIconsBankColored() {
  let a = document.querySelectorAll('i[class^="ib"]');
  for (let n = 0; n < a.length; n++) {
    let e = iconsBankColoredPaths(a[n].classList[1]);
    if (e > 1)
      for (let o = 1; o <= e; o++)
        a[n].innerHTML += `<i class="ib path${o}"></i>`;
  }
}
document.addEventListener("DOMContentLoaded", () => {
  initIconsBankColored();
});
